mapper.select:
SELECT * FROM ( SELECT rownum rnum, A.*FROM( SELECT l.LAYERING_NO, l.USER_NO, l.PERFUME_NO1 , l.PERFUME_NO2, l.PERFUME1_PER, l.LAYERING_CONTENT, l.LAYERING_DATE , P1.PERFUME_NAME Pname1, P2.perfume_name Pname2 , F1.file_no file1, F1.origin_name fileO1, F1.stored_name fileS1 , F2.file_no file2, F2.origin_name fileO2, F2.stored_name fileS2 , u.nick , F3.stored_name PROFILE , (SELECT count(*) FROM tb_layer_like where layering_no = l.layering_no) SCnt ,(SELECT count(*) FROM tb_layer_like WHERE layering_no = l.layering_no AND user_no = #{userNo }) SLike FROM tb_layer l LEFT OUTER JOIN tb_user u ON l.user_no = u.user_no LEFT OUTER JOIN tb_perf P1 ON l.PERFUME_NO1 = P1.PERFUME_NO LEFT OUTER JOIN tb_perf P2 ON l.PERFUME_NO2 = P2.PERFUME_NO LEFT OUTER JOIN tb_file F1 ON P1.file_no = F1.file_no LEFT OUTER JOIN tb_file F2 ON P2.file_no = F2.file_no LEFT OUTER JOIN tb_file F3 ON u.file_no = F3.file_no
ORDER BY LAYERING_NO DESC
ORDER BY SCnt DESC
ORDER BY LAYERING_NO DESC
)A )layer_list WHERE rnum BETWEEN #{paging.startNo } AND #{paging.endNo }

SELECT * FROM ( SELECT rownum rnum, L.* FROM ( SELECT P.PERFUME_NO, P.BRAND_NO, P.FILE_NO , P.PERFUME_NAME, P.PERFUME_VITALITY, P.PERFUME_GENDER , F.ORIGIN_NAME, F.STORED_NAME, F.FILE_SIZE , B.BRAND_NAME FROM tb_perf P LEFT OUTER JOIN tb_file F ON P.FILE_NO = F.FILE_NO LEFT OUTER JOIN tb_brand B ON P.BRAND_NO = B.BRAND_NO
WHERE PERFUME_NAME LIKE '%' || #{search } || '%'
ORDER BY P.PERFUME_NO ASC ) L ) PerfList WHERE rnum BETWEEN #{startNo } AND #{endNo }
